mapper.select:
select 
from cskaoyan_mall_region
where pid = #{pid}

select id,name,`desc`,pic_url as picUrl, sort_order as sortOrder, floor_price as floorPrice, add_time as addTime , update_time as updateTime, deleted from cskaoyan_mall_brand
and id = #{brand.id}
and name like '%' #{brand.name} '%'

mapper.update:
update cskaoyan_mall_brand set `desc` = #{brand.desc}, floor_price = #{brand.floorPrice}
, name = #{brand.name} , pic_url = #{brand.picUrl}, update_time = #{brand.updateTime}
where id = #{brand.id}

mapper.select:
select id, user_id as userId , order_sn as orderSn ,order_status as orderStatus ,consignee ,mobile,address ,message ,goods_price as goodsPrice ,freight_price as freightPrice ,coupon_price as couponPrice ,integral_price as integralPrice ,groupon_price as grouponPrice ,order_price as orderPrice ,actual_price as actualPrice ,pay_id as payId ,pay_time as payTime , ship_sn as shipSn ,ship_channel as shipChannel ,ship_time as shipTime ,confirm_time as confirmTime ,comments ,end_time as endTime ,add_time as addTime ,update_time as updateTime,deleted from cskaoyan_mall_order
and user_id = #{order.userId}
and order_sn = #{order.orderSn}
and
or order_status = #{orderStatus}

mapper.update:
update cskaoyan_mall_keyword set  is_default = #{keyword.isDefault}
, is_hot = #{keyword.isHot} ,keyword = #{keyword.keyword},update_time = #{keyword.updateTime}
, url = #{keyword.url}
where id = #{keyword.id}

mapper.select:
select keyword
from cskaoyan_mall_search_history
where user_id = #{userId}

mapper.update:
update cskaoyan_mall_category set `desc` = #{category.desc}, icon_url = #{category.iconUrl}
, keywords = #{category.keywords} ,level = #{category.level}
, name = #{category.name}, pic_url = #{category.picUrl}
