mapper.select:
select count(cu.id)
from cskaoyan_mall_coupon_user cu
LEFT JOIN cskaoyan_mall_coupon c
on cu.coupon_id = c.id
where cu.user_id = #{userId}
and c.min <= #{totalPrice}

select c.discount as price
from cskaoyan_mall_coupon_user cu
LEFT JOIN cskaoyan_mall_coupon c
on cu.coupon_id = c.id
where cu.user_id = #{userId}
and cu.coupon_id = #{couponId}

mapper.insert:

            select last_insert_Id() as id

        insert into cskaoyan_mall_order
        (
        user_id
        ,order_sn
        ,order_status
        ,consignee
        ,mobile
        ,address
        ,message
        ,goods_price
        ,freight_price
        ,coupon_price
        ,integral_price
        ,groupon_price
        ,order_price
        ,actual_price
        ,pay_id
        ,ship_sn
        ,add_time
        ,confirm_time
        ,update_time
        )
        values
        (   #{order.userId}
		 ,#{order.orderSn}
		 ,#{order.orderStatus}
		 ,#{order.consignee}
		 ,#{order.mobile}
		 ,#{order.address}
		 ,#{order.message}
		 ,#{order.goodsPrice}
		 ,#{order.freightPrice}
		 ,#{order.couponPrice}
